import { useEffect, useState } from "react";
import { Doctor, Patient } from "./interfaces";
import { formatDate, formatTime } from "./utils";

interface Props {
    patient: Patient | null;
}

export const DoctorBookingForm = (props: Props) => {
    const [doctors, setDoctors] = useState<Doctor[]>([]);
    const [selectedDate, setSelectedDate] = useState<string>("");
    const [selectedTime, setSelectedTime] = useState<string>("");
    const [duration, setDuration] = useState<number>();

    useEffect(() => {
        const fetchDoctors = async () => {
            try {
                const response = await fetch("http://localhost:8080/api/doctor");
                const data = await response.json();
                setDoctors(data);
            } catch (error) {
                console.log(error);
            }
        };
        fetchDoctors();
    }, []);

    const handleFormSubmit = (event: any) => {
        event.preventDefault();
        // TODO: add logic to submit form based on booking option
    };

    const handleDurationChange = (event: any) => {
      const newDuration = parseInt(event.target.value);
      setDuration(newDuration);
    };	

    const handleDateChange = (event: any) => {
      const date = event.target.value;
      console.log(date)
      const formattedDate = formatDate(date);
      setSelectedDate(formattedDate);
      setSelectedDate(date);
    };

    const handleTimeChange = (event: any) => {
      const time = event.target.value;
      console.log(time)
      const formattedTime = formatTime(time);
      setSelectedTime(formattedTime);
    };

    return (
        <form onSubmit={handleFormSubmit}>
            {doctors && <label htmlFor="doctor">Select a Doctor:
              <select id="doctor" name="doctor">
                  {doctors.map((doctor) => (
                      <option key={doctor.id} value={doctor.id}>
                          {doctor.firstName} {doctor.lastName}
                      </option>
                  ))}
              </select>
            </label>}
            <button type="submit">Book Appointment</button>
        </form>
    );
};
