import { useState, useEffect } from "react";
import { Facility } from "./interfaces";

const FacilityBookingForm = () => {
  const [facilities, setFacilities] = useState<Facility[] | []>([]);
	const [selectedFacility, setSelectedFacility] = useState<Facility | null>(null);


  useEffect(() => {
    const fetchFacilities = async () => {
      try {
        const response = await fetch("http://localhost:8080/api/facility");
        const data = await response.json();
        setFacilities(data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchFacilities();
  }, []);

	const handleFacilityChange = (event: any) => {
    const facility = event.target.value;
    setSelectedFacility(facility);
  };

	const handleSpecializationChange = (event: any) => {
    const facility = event.target.value;
    setSelectedFacility(facility);
  };

  return (
    <div>
      <h3>Book by Facility</h3>
      <form>
        <label>
          Select a facility:
          <select onChange={handleFacilityChange}>
            {facilities.map((facility) => (
              <option key={facility.id} value={facility.id}>
                {facility.name}
              </option>
            ))}
          </select>
        </label>
        <br />
				<label>
          Select a specialization:
          <select onChange={handleFacilityChange}>
            {selectedFacility?.specializations.map((specialization) => (
              <option key={specialization.id} value={specialization.id}>
                {specialization.name}
              </option>
            ))}
          </select>
        </label>
        <button type="submit">Book</button>
      </form>
    </div>
  );
};

export default FacilityBookingForm;
