import { useState } from "react";
import { Patient } from "./interfaces";
import { useLocation } from "react-router-dom";

const PatientAccount = () => {
  const location = useLocation();
  const patient: Patient = location.state;
  const [editablePatient, setEditablePatient] = useState(patient);

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setEditablePatient((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSave = (event: any) => {

  }

  return (
    <div>
      <h2>My Account</h2>
      <form>
        <div>
          <label htmlFor="firstName">First Name:</label>
          <input
            type="text"
            name="firstName"
            value={editablePatient.firstName}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="lastName">Last Name:</label>
          <input
            type="text"
            name="lastName"
            value={editablePatient.lastName}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="phoneNumber">Phone Number:</label>
          <input
            type="text"
            name="phoneNumber"
            value={editablePatient.phoneNumber}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="address">Address:</label>
          <input
            type="text"
            name="address"
            value={editablePatient.address}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="city">City:</label>
          <input
            type="text"
            name="city"
            value={editablePatient.city}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="zipCode">Zip Code:</label>
          <input
            type="text"
            name="zipCode"
            value={editablePatient.zipCode}
            onChange={handleInputChange}
          />
        </div>
        <button type="button" onClick={handleSave}>
          Save Changes
        </button>
      </form>
    </div>
  );
};

export default PatientAccount;
